      image: "https://images.com/concert3.jpg"
    },
    {
      id: 4,
      name: "Concert 4",
      artist: "Artist 4",
      date: "04/01/2022",
      image: "https://images.com/concert4.jpg"
    },
    {
      id: 5,
      name: "Concert 5",
      artist: "Artist 5",
      date: "05/01/2022",
      image: "https://images.com/concert5.jpg"
    }
  ]

  const [events, setEvents] = useState(concerts);


  return (
    <div>
      <h2>Upcoming Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <img src={event.image} alt={event.name} />
            <h3>{event.name}</h3>
            <p>{event.artist}</p>
            <p>{event.date}</p>
            <button>View Event</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UpcomingEvents;
