import React, { useState, useEffect } from 'react';
import "./UpcomingEvents.css";

const UpcomingEvents = () => {

  // useEffect(() => {
  //   async function fetchEvents() {
  //     const res = await fetch('https://api.myconcerts.com/upcoming');
  //     const data = await res.json();
  //     setEvents(data);
  //   }
  //   fetchEvents();
  // }, []);

  const concerts = [
    {
      id: 1,
      name: "Concert 1",
      artist: "Artist 1",
      date: "01/01/2022",
      image: "https://images.com/concert1.jpg"
    },
    {
      id: 2,
      name: "Concert 2",
      artist: "Artist 2",
      date: "02/01/2022",
      image: "https://images.com/concert2.jpg"
    },
    {
      id: 3,
      name: "Concert 3",
      artist: "Artist 3",
      date: "03/01/2022",
      image: "https://images.com/concert3.jpg"
    },
    {
      id: 4,
      name: "Concert 4",
      artist: "Artist 4",
      date: "04/01/2022",
      image: "https://images.com/concert4.jpg"
    },
    {
      id: 5,
      name: "Concert 5",
      artist: "Artist 5",
      date: "05/01/2022",
      image: "https://images.com/concert5.jpg"
    }
  ]

  const [events, setEvents] = useState(concerts);


  return (
    <div className="upcoming-events">
      <h2>Upcoming Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            <img src={event.image} alt={event.name} />
            <h3>{event.name}</h3>
            <p>{event.artist}</p>
            <p>{event.date}</p>
            <button>View Event</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default UpcomingEvents;
